def get_record(url):
    resp = urllib.request.urlopen(url)
    ele_json = json.loads(resp.read())
    return ele_json
def get_time(ystime):
    timeStamp = ystime
    dateArray = datetime.fromtimestamp(timeStamp)
    otherStyleTime = dateArray.strftime("%Y年%m月%d日%H时%M分%S秒")
    return otherStyleTime
